import React from 'react'
import "../styles/ListItem.css"

export default function ListItem({item, user}) {

    return (
        <div>
            {
                item.details !== "" ? (
                    <div className={"msgContainer"}>
                        <p className={"timestamp"}>{item.timestamp}</p>

                        {
                            // if the message is from you, set color
                            user.name === item.user ? (
                                <p className={"msgInfo"} style={{color: user.color}}>{item.user}</p>
                            ) : (
                                <p className={"msgInfo"}>{item.user}</p>
                            )
                        }

                        <p className={"regularText"}>Hi</p>
                    </div>

                ) : (
                    <p className={"regularText"}>{item.user}</p>
                )
            }
        </div >
    )
}
