import React, {useState} from 'react'
import "../styles/MessageInput.css"

export default function MessageInput({addNewMessage, socket, user}) {

    const [currentMessage, setCurrentMessage] = useState('')

    const handleChange = (e) => {
        setCurrentMessage(e.target.value)
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        // socket.emit('chat message', {username: user.name, message: e.value})
        let newMessage = {type: "message", username: user.username, timestamp: "17:08", msg: currentMessage}
        addNewMessage(prevMessages => [...prevMessages, newMessage])
    }

    return (
        <form className={"form"} onSubmit={handleSubmit}>
            <input type={"text"} className={"input"} placeholder={"Type in a message..."} onChange={handleChange}/>
        </form>
    )
}
