import React, {useEffect, useState} from 'react'
import "../styles/Message.css"

export default function Message({item, onlineUsers, user}) {

    const [prevSender, setPrevSender] = useState('')
    const [sameUser, setSameUser] = useState(false)

    useEffect(() => {
        if (item.socketId === prevSender) {
            setSameUser(true)
        } else {
            setSameUser(false)
        }
        setPrevSender(item.socketId)
    }, [item])

    const getCorrectColor = () => {
        let color = "black"
        if (item && user) {
            let correctUser = onlineUsers.filter(object => {
                return object.username === item.username
            })
            if (correctUser.length > 0) {
                color = correctUser[0].color
            }
        }
        return color
    }

    const getTextStyle = () => {
        let textStyle = ""
        if (item && user) {
            if (item.socketId === user.socketId) {
                textStyle = " bold"
            }
        }
        return textStyle
    }

    const getSpacing = () => {
        return " " + item.type + "Spacing"
    }

    const getYouText = () => {
        if (item.username === user.username && item.type === "users") {
            return " (You)"
        }
        return ""
    }

    return (
        <div className={"entireMessage" + getSpacing()}>
            <div className={"msgInfo"}>
                {item.type === "users" || sameUser && <p className={"name" + getTextStyle()}
                    style={{color: getCorrectColor()}}>{item.username + getYouText()}</p>}
                {item.type === "message" && <p className={"timestamp text" + getTextStyle()}>at {item.timestamp}</p>}
            </div>
            {item.type === "message" && <p className={"text"}>{item.msg}</p>}
        </div>
    )
}
