import React from 'react'
import "../styles/Message.css"

export default function Message({item, onlineUsers, user}) {

    const emojiMap = {
        ":)": "1F642", ":(": "1F641", ":o": "1F62E", ":D": "1F600", "<3": "2764"
    }

    const convertEmojis = (text) => {
        let re = new RegExp(/(:[o)(D]|<3)/, 'g')
        let match
        while (match = re.exec(text)) {
            let emojiString = String.fromCodePoint(parseInt(emojiMap[match[1]], 16))
            text = text.replace(match[1], emojiString)
        }
        return text
    }

    const getCorrectColor = () => {
        let color = "black"
        if (item) {
            // if (user) {
            //     let correctUser = onlineUsers.filter(object => {
            //         return object.username === item.username
            //     })
            //     if (correctUser.length > 0) {
            //         color = correctUser[0].color
            //     }
            // } else {
            //     color = mess
            // }
            color = item.color
        }
        return color
    }

    const getTextStyle = () => {
        let textStyle = ""
        if (item && user) {
            if (item.socketId === user.socketId) {
                textStyle = " bold"
            }
        }
        return textStyle
    }

    const getSpacing = () => {
        return " " + item.type + "Spacing"
    }

    const getYouText = () => {
        if (item.username === user.username && item.type === "users") {
            return " (You)"
        }
        return ""
    }

    return (
        <div className={"entireMessage" + getSpacing()}>
            <div className={"msgInfo"}>
                <p className={"name" + getTextStyle()} style={{color: getCorrectColor()}}>{item.username + getYouText()}</p>
                {item.type === "message" && <p className={"timestamp text"}>at {item.timestamp}</p>}
            </div>
            {item.type === "message" && <p className={"text"} id={"messageItem"}>{convertEmojis(item.msg)}</p>}
        </div>
    )
}
