import React from 'react'
import "../styles/Message.css"

export default function Message({item, onlineUsers, user}) {
    console.log("MESSAGE", onlineUsers)

    let textStyle = ""
    if (item.username === user.username) {
        textStyle = " bold"
    }

    const getCorrectColor = () => {
        let correctUser = onlineUsers.filter(object => {
            return object.username === item.username
        })
        return correctUser[0].color
    }

    return (
        <div className={"entireMessage"}>
            <div className={"msgInfo"}>
                <p className={"name" + textStyle} style={{color: getCorrectColor()}}>{item.username}</p>
                {item.type === "message" && <p className={"timestamp text" + textStyle}>at {item.timestamp}</p>}
            </div>
            {item.type === "message" && <p className={"text"}>{item.msg}</p>}
        </div>
    )
}
