import React, {useEffect, useState} from 'react'
import "../styles/ChatScreen.css"
import io from "socket.io-client"
import Header from "../components/Header";
import ChatContainer from "../components/ChatContainer";
import MessageInput from "../components/MessageInput";

const ENDPOINT = "http://localhost:5000/"

const socket = io(ENDPOINT)

export default function ChatScreen() {
    
    const [user, setUser] = useState()
    const [onlineUsers, setOnlineUsers] = useState([])
    const [messages, setMessages] = useState([])

    useEffect(() => {
        if (socket) {
            socket.on("new user", (user) => {
                setUser(user)
            })

            socket.on("updated users", ({users, messages}) => {
                setOnlineUsers(users)
                setMessages(messages)
            })
        }
    })


    // const user = {username: "Jackie", color: "dodgerblue"}
    // const onlineUsers = [
    //     {type: "online", username: "Jackie", color: "dodgerblue"},
    //     {type: "online", username: "Shreya", color: "tomato"}
    // ]
    // const messages = [{type: "message", username: "Jackie", timestamp: "17:08", msg: "Hi!!"}]

    return (
        <div className={"container"}>
            <Header text={"C H A T R O O M"} type={"main border"}/>
            <div className={"mainContainer"}>
                <ChatContainer header={true} listItems={onlineUsers} onlineUsers={onlineUsers} type={"online"} user={user}>
                    <Header text={"Online"} type={"sub"}/>
                </ChatContainer>
                <ChatContainer listItems={messages} onlineUsers={onlineUsers} type={"chat"} user={user}>
                    <MessageInput addNewMessage={setMessages} socket={socket} user={user}/>
                </ChatContainer>
            </div>
        </div>
    )
}
