import React from 'react'
import "../styles/MessageInput.css"

export default function MessageInput({addNewMessage, user}) {

    const handleSubmit = (e) => {
        e.preventDefault()
        addNewMessage()
    }

    return (
        <form className={"form"}>
            <input type={"text"} className={"input"} placeholder={"Type in a message..."} onSubmit={handleSubmit}/>
        </form>
    )
}
