import React, {useEffect, useState} from 'react'
import "../styles/ChatList.css"
import Message from "./Message";

export default function ChatList({listItems, onlineUsers, type, user}) {

    const [itemList, setItemList] = useState([])

    useEffect(() => {
        // sorting users for online bar so user is on top
        if (type === "online") {
            let onlineUsersList = listItems
            onlineUsersList = onlineUsersList.filter(u => {
                return u.username !== user.username
            })
            onlineUsersList.unshift(user)
            setItemList(onlineUsersList)
        } else {
            setItemList(listItems)
        }

        console.log("ITEMLIST:", itemList)
    }, [listItems])


    return (
        <div className={"chatList " + type + "Box"} id={type}>
            <div className={"scrollContainer"}>
                {
                    typeof(listItems) !== typeof(2) && listItems.length > 0 && listItems.map(item => {
                        return <Message item={itemList} onlineUsers={onlineUsers} user={user}/>
                    })
                }
            </div>
        </div>
    )
}
