import React from 'react'
import "../styles/ChatContainer.css"
import ChatList from "./ChatList";

export default function ChatContainer({backgroundColor, children, listItems, user, width="100%"}) {
    return (
        <div className={"chatContainer"} style={{backgroundColor: backgroundColor, width: width}}>
            {children}
            <ChatList listItems={listItems} user={user}/>
        </div>
    )
}
