import React from 'react'
import "../styles/MessageInput.css"

export default function MessageInput({addNewMessage=1, socket, user}) {

    const handleSubmit = (e) => {
        e.preventDefault()
        socket.emit('chat message', {username: user.name, message: e.value})
    }

    return (
        <form className={"form"} onSubmit={handleSubmit}>
            <input type={"text"} className={"input"} placeholder={"Type in a message..."} />
        </form>
    )
}
