import React, {useState} from 'react'
import "../styles/ChatScreen.css"
import Header from "../components/Header";
import ChatList from "../components/ChatList";

export default function ChatScreen() {

    const [user, setUser] = useState({
        name: "RandomUser",
        color: "black"
    })

    const online_items = [
        {user: "Jackie", timestamp: "", details: ""},
        {user: "Armin", timestamp: "", details: ""},
        {user: "Shreya", timestamp: "", details: ""},
        {user: "Ross", timestamp: "", details: ""},
        {user: "Alexa", timestamp: "", details: ""}
    ]

    const message_items = [
        {user: "Jackie", timestamp: "17:08", details: "Hi!!"},
        {user: "Armin", timestamp: "18:16", details: "Hey"},
    ]

    return (
        <div className={"container"}>
            <Header text={"ChatRoom"}/>
            <div className={"main"}>
                <ChatList backgroundColor={"tomato"} width={"300px"} listItems={online_items} user={user}>
                    <Header text={"Online"}/>
                </ChatList>
                <ChatList backgroundColor={"gold"} listItems={message_items} user={user}/>
            </div>
        </div>
    )
}
