import React from 'react'
import "../styles/ChatList.css"
import ListItem from "./ListItem";

export default function ChatList({backgroundColor, children, listItems, user, width="100%"}) {
    console.log(listItems)
    return (
        <div className={"chatlist"} style={{backgroundColor: backgroundColor, width: width}}>
            {children}
            {
                listItems && listItems.map(item => {
                    return <ListItem item={item} user={user}/>
                })
            }
        </div>
    )
}
