import React from 'react'
import "../styles/ChatContainer.css"
import ChatList from "./ChatList";
import MessageInput from "./MessageInput";

export default function ChatContainer({children, listItems, type, user}) {
    return (
        <div className={type}>
            {children}
            <ChatList listItems={listItems} user={user}/>
            <MessageInput/>
        </div>
    )
}
