import React, {useState} from 'react'
import "../styles/Message.css"

export default function Message({item, onlineUsers, user}) {

    const getTextStyle = () => {
        let textStyle = ""
        if (item && user) {
            if (item.username === user.username) {
                textStyle = " bold"
            }
        }
        return textStyle
    }

    const getCorrectColor = () => {
        let color = "black"
        if (item && user) {
            let correctUser = onlineUsers.filter(object => {
                return object.username === item.username
            })
            if (correctUser.length > 0) {
                color = correctUser[0].color
            }
        }
        return color
    }

    return (
        <div className={"entireMessage"}>
            <div className={"msgInfo"}>
                <p className={"name" + getTextStyle()} style={{color: getCorrectColor()}}>{item.username}</p>
                {item.type === "message" && <p className={"timestamp text" + getTextStyle()}>at {item.timestamp}</p>}
            </div>
            {item.type === "message" && <p className={"text"}>{item.msg}</p>}
        </div>
    )
}
