import React from 'react'
import "../styles/ListItem.css"
import Message from "./Message";

export default function ListItem({item, user}) {

    return (
        <div className={"listItem"}>
            {
                item.details !== "" ? (
                    <>
                        {
                            user.name === item.user ? (
                                <div className={"msgDiv"}>
                                    <Message item={item} user={user}/>
                                </div>
                            ) : (
                                <div className={"msgDiv"}>
                                    <div className={"msgInfo"}>
                                        <p className={"name"} style={{color: user.color}}>{item.user}</p>
                                        <p className={"timestamp text"}>at {item.timestamp}</p>
                                    </div>

                                    <p className={"text"}>Hi</p>
                                </div>
                            )
                        }


                    </>

                ) : (
                    <p className={"text"}>{item.user}</p>
                )
            }
        </div >
    )
}
