import React, {useEffect, useState} from 'react'
import "../styles/ChatScreen.css"
import io from "socket.io-client"
import Header from "../components/Header";
import ChatContainer from "../components/ChatContainer";
import MessageInput from "../components/MessageInput";

const ENDPOINT = "http://localhost:5000/"

export default function ChatScreen() {

    const [socket, setSocket] = useState()
    const [user, setUser] = useState()
    const [onlineUsers, setOnlineUsers] = useState([])
    const [messages, setMessages] = useState([])

    useEffect(() => {
        console.log("SETTING SOCKET")
        setSocket(io(ENDPOINT))
        return () => {
            socket.disconnect();
        }
    }, [])

    useEffect(() => {
        if (socket) {
            socket.on("new user", (user) => {
                console.log("NEW USER:", user)
                setUser(user)
            })

            socket.on("updated users", ({users, messages}) => {
                console.log("UPDATED USERS:", users, messages)
                setOnlineUsers(users)
                setMessages(messages)
            })
        }
    }, [socket])

    // const [user, setUser] = useState({
    //     username: "Jackie",
    //     color: "black"
    // })

    // const [messages, setMessages] = useState([])

    // const online_items = [
    //     {type: "users", username: "Jackie"}
    // ]

    // const message_items = [
    //     {type: "message", user: "Jackie", timestamp: "17:08", details: "Hi!!"},
    //     {user: "Armin", timestamp: "18:16", details: "Hey"},
    //     {user: "Armin", timestamp: "18:16", details: "Hey"},
    //     {user: "Armin", timestamp: "18:16", details: "Hey"},
    //     {user: "Armin", timestamp: "18:16", details: "Hey"},
    //     {user: "Armin", timestamp: "18:16", details: "This is a super duper long message! I want to see what happens if it overflows. It will probably look very ugly so I need to fix it. I need to add more text to see what happens! Hopefully I don't need to do more styling because I am about to kill myself."},
    //     {user: "Shreya", timestamp: "21:22", details: "What's up??????"},
    //     {user: "Alexa", timestamp: "00:18", details: "Goodnight!"},
    //     {user: "Ross", timestamp: "01:14", details: "Good morning."}
    // ]

    return (
        <div className={"container"}>
            <Header text={"C H A T R O O M"} type={"main border"}/>
            <div className={"mainContainer"}>
                <ChatContainer header={true} listItems={onlineUsers} type={"online"} user={user}>
                    <Header text={"Online"} type={"sub"}/>
                </ChatContainer>
                <ChatContainer listItems={messages} type={"chat"} user={user}>
                    <MessageInput addNewMessage={setMessages} socket={socket} user={user}/>
                </ChatContainer>


            </div>
        </div>
    )
}
