import React, {useState} from 'react'
import "../styles/MessageInput.css"

export default function MessageInput({addNewMessage, socket, user}) {

    const [inputFieldValue, setInputFieldValue] = useState('')
    const [currentMessage, setCurrentMessage] = useState('')

    const handleChange = (e) => {
        setCurrentMessage(e.target.value)
        setInputFieldValue(e.target.value)
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        if (currentMessage !== '') {
            console.log("HANDLE SUBMIT:", currentMessage)
            setInputFieldValue('')
            setCurrentMessage('')
            socket.emit('chat message', {msg: currentMessage})
        }
    }

    return (
        <form className={"form"} onSubmit={handleSubmit}>
            <input type={"text"} className={"input"} placeholder={"Type in a message..."} onChange={handleChange} value={inputFieldValue}/>
        </form>
    )
}
