import React, {useState} from 'react'
import "../styles/ChatScreen.css"
import Header from "../components/Header";
import ChatContainer from "../components/ChatContainer";
import MessageInput from "../components/MessageInput";

export default function ChatScreen() {

    const [user, setUser] = useState({
        name: "Jackie",
        color: "black"
    })

    const online_items = [
        {user: "Jackie", timestamp: "", details: ""},
        {user: "Armin", timestamp: "", details: ""},
        {user: "Shreya", timestamp: "", details: ""},
        {user: "Ross", timestamp: "", details: ""},
        {user: "Alexa", timestamp: "", details: ""}
    ]

    const message_items = [
        {user: "Jackie", timestamp: "17:08", details: "Hi!!"},
        {user: "Armin", timestamp: "18:16", details: "Hey"},
        {user: "Armin", timestamp: "18:16", details: "Hey"},
        // {user: "Armin", timestamp: "18:16", details: "Hey"},
        // {user: "Armin", timestamp: "18:16", details: "Hey"},
        // {user: "Armin", timestamp: "18:16", details: "Hey"},
        // {user: "Armin", timestamp: "18:16", details: "Hey"},
        // {user: "Armin", timestamp: "18:16", details: "Hey"},
        // {user: "Armin", timestamp: "18:16", details: "Hey"},
        // {user: "Armin", timestamp: "18:16", details: "Hey"},
        // {user: "Armin", timestamp: "18:16", details: "Hey"},
        // {user: "Armin", timestamp: "18:16", details: "Hey"},


    ]

    return (
        <div className={"container"}>
            <Header text={"C H A T R O O M"} type={"main border"}/>
            <div className={"mainContainer"}>
                <ChatContainer header={true} listItems={online_items} type={"online"} user={user}>
                    <Header text={"Online"} type={"sub"}/>
                </ChatContainer>
                <ChatContainer listItems={message_items} type={"chat"} user={user}>
                    <MessageInput/>
                </ChatContainer>


            </div>
        </div>
    )
}
